// Process and analyze campaign performance data
const instantlyCampaigns = $('fetch-instantly-campaigns').first().json.data || [];
const localCampaigns = $('fetch-local-campaigns').all().map(item => item.json);

// Create performance analysis for each campaign
const campaignAnalysis = [];

for (const campaign of instantlyCampaigns) {
  const localData = localCampaigns.find(local => local.instantly_campaign_id === campaign.id);

  if (!localData) continue; // Skip campaigns not in our system

  // Calculate performance metrics
  const totalSent = campaign.stats?.total_sent || 0;
  const totalOpened = campaign.stats?.total_opened || 0;
  const totalClicked = campaign.stats?.total_clicked || 0;
  const totalReplied = campaign.stats?.total_replied || 0;
  const totalBounced = campaign.stats?.total_bounced || 0;
  const totalUnsubscribed = campaign.stats?.total_unsubscribed || 0;

  // Calculate rates
  const openRate = totalSent > 0 ? (totalOpened / totalSent * 100).toFixed(2) : 0;
  const clickRate = totalOpened > 0 ? (totalClicked / totalOpened * 100).toFixed(2) : 0;
  const replyRate = totalSent > 0 ? (totalReplied / totalSent * 100).toFixed(2) : 0;
  const bounceRate = totalSent > 0 ? (totalBounced / totalSent * 100).toFixed(2) : 0;
  const unsubscribeRate = totalSent > 0 ? (totalUnsubscribed / totalSent * 100).toFixed(2) : 0;

  // Performance scoring (0-100)
  let performanceScore = 0;
  performanceScore += Math.min(openRate * 2, 50); // Open rate worth up to 50 points
  performanceScore += Math.min(clickRate * 5, 25); // Click rate worth up to 25 points
  performanceScore += Math.min(replyRate * 10, 25); // Reply rate worth up to 25 points
  performanceScore = Math.max(0, performanceScore - (bounceRate * 2)); // Penalty for bounces
  performanceScore = Math.max(0, performanceScore - (unsubscribeRate * 5)); // Penalty for unsubscribes

  // Determine performance status
  let performanceStatus = 'poor';
  if (performanceScore >= 80) performanceStatus = 'excellent';
  else if (performanceScore >= 60) performanceStatus = 'good';
  else if (performanceScore >= 40) performanceStatus = 'average';

  // Generate recommendations
  const recommendations = [];

  if (openRate < 20) {
    recommendations.push('Improve subject lines - open rate below industry average');
  }
  if (clickRate < 3 && totalOpened > 50) {
    recommendations.push('Optimize email content and CTAs - low click-through rate');
  }
  if (replyRate < 1 && totalSent > 100) {
    recommendations.push('Personalize outreach and improve value proposition');
  }
  if (bounceRate > 5) {
    recommendations.push('URGENT: Review email list quality - high bounce rate affecting deliverability');
  }
  if (unsubscribeRate > 2) {
    recommendations.push('Review targeting and message relevance - high unsubscribe rate');
  }

  // Delivery health assessment
  let deliveryHealth = 'good';
  if (bounceRate > 10 || unsubscribeRate > 3) {
    deliveryHealth = 'critical';
  } else if (bounceRate > 5 || unsubscribeRate > 1.5) {
    deliveryHealth = 'warning';
  }

  // A/B test opportunities
  const abTestOpportunities = [];
  if (openRate < 25) abTestOpportunities.push('subject_lines');
  if (clickRate < 3) abTestOpportunities.push('email_content');
  if (replyRate < 1.5) abTestOpportunities.push('personalization');

  campaignAnalysis.push({
    // Campaign identification
    campaignId: campaign.id,
    campaignName: campaign.name,
    localCampaignId: localData.id,
    targetServiceTier: localData.target_service_tier,

    // Performance metrics
    totalSent,
    totalOpened,
    totalClicked, 
    totalReplied,
    totalBounced,
    totalUnsubscribed,

    // Calculated rates
    openRate: parseFloat(openRate),
    clickRate: parseFloat(clickRate),
    replyRate: parseFloat(replyRate),
    bounceRate: parseFloat(bounceRate),
    unsubscribeRate: parseFloat(unsubscribeRate),

    // Analysis results
    performanceScore: Math.round(performanceScore),
    performanceStatus,
    deliveryHealth,
    recommendations,
    abTestOpportunities,

    // Action flags
    needsImprovement: performanceScore < 50,
    requiresUrgentAttention: bounceRate > 10 || unsubscribeRate > 3,
    readyForScaling: performanceScore > 70 && deliveryHealth === 'good',

    // Metadata
    analysisTimestamp: new Date().toISOString(),
    lastUpdated: campaign.updated_at
  });
}

// Sort by performance score (lowest first for priority attention)
campaignAnalysis.sort((a, b) => a.performanceScore - b.performanceScore);

return campaignAnalysis.map(analysis => ({ json: analysis }));

