// Process AI optimization recommendations
const campaignData = $('check-improvement-needs').first().json;
const claudeResponse = $('generate-optimization-recommendations').first().json;

let optimizationPlan = {
  campaignId: campaignData.campaignId,
  campaignName: campaignData.campaignName,
  performanceScore: campaignData.performanceScore,
  currentIssues: campaignData.recommendations,
  aiRecommendations: null,
  actionPlan: [],
  priority: 'medium'
};

// Parse Claude's response
try {
  if (claudeResponse.content && claudeResponse.content[0] && claudeResponse.content[0].text) {
    const analysisText = claudeResponse.content[0].text;
    const jsonMatch = analysisText.match(/\{[\s\S]*\}/);

    if (jsonMatch) {
      const aiRecommendations = JSON.parse(jsonMatch[0]);
      optimizationPlan.aiRecommendations = aiRecommendations;

      // Create prioritized action plan
      const actionPlan = [];

      // Priority 1: Fix urgent issues first
      if (campaignData.bounceRate > 5) {
        actionPlan.push({
          priority: 1,
          action: 'Clean email list and improve data quality',
          impact: 'high',
          effort: 'medium',
          timeline: 'immediate'
        });
      }

      // Priority 2: Improve low-performing metrics
      if (campaignData.openRate < 20) {
        actionPlan.push({
          priority: 2,
          action: 'A/B test new subject lines',
          details: aiRecommendations.subject_lines?.slice(0, 3) || ['Test urgent vs value-based subjects'],
          impact: 'high',
          effort: 'low',
          timeline: 'this_week'
        });
      }

      if (campaignData.clickRate < 2) {
        actionPlan.push({
          priority: 2,
          action: 'Optimize email content and CTAs',
          details: aiRecommendations.content_optimization || 'Improve value proposition and CTA placement',
          impact: 'medium',
          effort: 'medium',
          timeline: 'this_week'
        });
      }

      if (campaignData.replyRate < 1) {
        actionPlan.push({
          priority: 3,
          action: 'Enhance personalization',
          details: aiRecommendations.personalization || 'Add company-specific value propositions',
          impact: 'medium',
          effort: 'high',
          timeline: 'next_week'
        });
      }

      // Add A/B testing opportunities
      if (aiRecommendations.ab_tests) {
        actionPlan.push({
          priority: 3,
          action: 'Implement A/B tests',
          details: aiRecommendations.ab_tests,
          impact: 'medium',
          effort: 'low',
          timeline: 'ongoing'
        });
      }

      optimizationPlan.actionPlan = actionPlan;
      optimizationPlan.priority = campaignData.performanceScore < 30 ? 'high' : 
                                 campaignData.performanceScore < 50 ? 'medium' : 'low';
    }
} catch (error) {
  optimizationPlan.aiRecommendations = {
    error: 'Failed to parse AI recommendations',
    raw_response: claudeResponse
  };

  // Fallback action plan
  optimizationPlan.actionPlan = [
    {
      priority: 1,
      action: 'Manual campaign review required',
      impact: 'unknown',
      effort: 'high',
      timeline: 'asap'
    }
  ];
}

return [{ json: optimizationPlan }];

// Generate comprehensive campaign performance summary
const allCampaigns = $input.first().json;
const campaigns = Array.isArray(allCampaigns) ? allCampaigns : [allCampaigns];

// Calculate overall performance metrics
const totalCampaigns = campaigns.length;
const activeCampaigns = campaigns.filter(c => !c.requiresUrgentAttention).length;
const problematicCampaigns = campaigns.filter(c => c.requiresUrgentAttention).length;
const highPerformingCampaigns = campaigns.filter(c => c.readyForScaling).length;
const needImprovementCampaigns = campaigns.filter(c => c.needsImprovement).length;

// Average performance metrics
const avgPerformanceScore = campaigns.reduce((sum, c) => sum + c.performanceScore, 0) / totalCampaigns;
const avgOpenRate = campaigns.reduce((sum, c) => sum + c.openRate, 0) / totalCampaigns;
const avgReplyRate = campaigns.reduce((sum, c) => sum + c.replyRate, 0) / totalCampaigns;
const avgBounceRate = campaigns.reduce((sum, c) => sum + c.bounceRate, 0) / totalCampaigns;

// Identify top and bottom performers
const topPerformer = campaigns.reduce((best, current) => 
  current.performanceScore > best.performanceScore ? current : best
);
const bottomPerformer = campaigns.reduce((worst, current) => 
  current.performanceScore < worst.performanceScore ? current : worst
);

// Generate insights
const insights = [];

if (avgOpenRate < 20) {
  insights.push('Overall open rates below industry standard - focus on subject line optimization');
}
if (avgReplyRate < 1.5) {
  insights.push('Low reply rates across campaigns - improve personalization and value propositions');
}
if (problematicCampaigns > 0) {
  insights.push(`${problematicCampaigns} campaign(s) require immediate attention due to deliverability issues`);
}
if (highPerformingCampaigns > 0) {
  insights.push(`${highPerformingCampaigns} campaign(s) ready for scaling - consider increasing volume`);
}

// Service tier analysis
const tierPerformance = {};
campaigns.forEach(campaign => {
  const tier = campaign.targetServiceTier || 'unknown';
  if (!tierPerformance[tier]) {
    tierPerformance[tier] = {
      count: 0,
      totalScore: 0,
      totalReplies: 0,
      totalSent: 0
    };
  }
  tierPerformance[tier].count++;
  tierPerformance[tier].totalScore += campaign.performanceScore;
  tierPerformance[tier].totalReplies += campaign.totalReplied;
  tierPerformance[tier].totalSent += campaign.totalSent;
});

// Calculate tier averages
Object.keys(tierPerformance).forEach(tier => {
  const data = tierPerformance[tier];
  data.avgScore = Math.round(data.totalScore / data.count);
  data.avgReplyRate = data.totalSent > 0 ? (data.totalReplies / data.totalSent * 100).toFixed(2) : 0;
});

const summary = {
  // Overview
  totalCampaigns,
  activeCampaigns,
  problematicCampaigns,
  highPerformingCampaigns,
  needImprovementCampaigns,

  // Performance averages
  avgPerformanceScore: Math.round(avgPerformanceScore),
  avgOpenRate: Math.round(avgOpenRate * 100) / 100,
  avgReplyRate: Math.round(avgReplyRate * 100) / 100,
  avgBounceRate: Math.round(avgBounceRate * 100) / 100,

  // Top/bottom performers
  topPerformer: {
    name: topPerformer.campaignName,
    score: topPerformer.performanceScore,
    replyRate: topPerformer.replyRate
  },
  bottomPerformer: {
    name: bottomPerformer.campaignName,
    score: bottomPerformer.performanceScore,
    replyRate: bottomPerformer.replyRate
  },

  // Service tier analysis
  tierPerformance,

  // Insights and recommendations
  insights,

  // Status counts by tier
  tierBreakdown: Object.keys(tierPerformance).map(tier => ({
    tier,
    campaigns: tierPerformance[tier].count,
    avgScore: tierPerformance[tier].avgScore,
    avgReplyRate: tierPerformance[tier].avgReplyRate
  })),

  // Metadata
  analysisDate: new Date().toISOString(),
  nextAnalysis: new Date(Date.now() + 4 * 60 * 60 * 1000).toISOString() // 4 hours from now
};

return [{ json: summary }];

