// Parse Claude AI analysis and prepare database update
const claudeResponse = $input.first().json;
const activityData = $('process-activity-data').first().json;

let aiAnalysis = {};
let leadQualificationUpdate = false;
let suggestedFollowUp = '';
let updatedSentiment = activityData.reply_sentiment;
let updatedIntent = activityData.reply_intent;

// Try to parse Claude's response
try {
  if (claudeResponse.content && claudeResponse.content[0] && claudeResponse.content[0].text) {
    const analysisText = claudeResponse.content[0].text;

    // Extract JSON from Claude's response
    const jsonMatch = analysisText.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      const parsedAnalysis = JSON.parse(jsonMatch[0]);

      // Update values with Claude's analysis
      updatedSentiment = parsedAnalysis.sentiment || activityData.reply_sentiment;
      updatedIntent = parsedAnalysis.intent || activityData.reply_intent;
      suggestedFollowUp = parsedAnalysis.suggested_action || 'Manual review required';

      aiAnalysis = {
        claude_sentiment: parsedAnalysis.sentiment,
        claude_intent: parsedAnalysis.intent,
        qualification_status: parsedAnalysis.qualification,
        suggested_action: parsedAnalysis.suggested_action,
        priority_level: parsedAnalysis.priority,
        talking_points: parsedAnalysis.talking_points,
        ai_confidence: 'high',
        analysis_timestamp: new Date().toISOString()
      };

      // Determine if lead qualification should be updated
      if (parsedAnalysis.qualification && 
          ['qualified', 'sales_qualified', 'marketing_qualified'].includes(parsedAnalysis.qualification)) {
        leadQualificationUpdate = true;
      }
    }
  }
} catch (error) {
  // Fallback if Claude's response can't be parsed
  aiAnalysis = {
    error: 'Failed to parse AI response',
    raw_response: claudeResponse,
    ai_confidence: 'low',
    analysis_timestamp: new Date().toISOString()
  };
  suggestedFollowUp = 'Manual review required - AI analysis failed';
}

const analysisResult = {
  activityId: activityData.instantly_activity_id,
  integrationId: activityData.integration_id,
  companyId: activityData.company_id,
  updatedSentiment: updatedSentiment,
  updatedIntent: updatedIntent,
  aiAnalysis: JSON.stringify(aiAnalysis),
  suggestedFollowUp: suggestedFollowUp,
  leadQualificationUpdate: leadQualificationUpdate,
  qualificationStatus: aiAnalysis.qualification_status,
  priorityLevel: aiAnalysis.priority_level || 'medium',
  shouldNotifyTeam: aiAnalysis.priority_level === 'high' || updatedSentiment === 'interested'
};

return [{ json: analysisResult }];

