// Enhanced prospect data preparation for Instantly integration
const companyData = $('fetch-company-data').first().json;
const opportunities = $('fetch-automation-opportunities').all().map(item => item.json);
const webhookData = $('crm-webhook').first().json;

// Calculate comprehensive lead metrics
let leadScore = 0;
let totalROI = 0;
let recommendedPackage = 'basic_2_5k';
let priorityLevel = 'medium';

// Company size scoring (0-25 points)
const companySizeScores = {
  'startup': 15,
  'small': 18,
  'medium': 22,
  'large': 25,
  'enterprise': 25
};
leadScore += companySizeScores[companyData.company_size_category] || 15;

// Industry scoring (0-20 points)
const highValueIndustries = [
  'professional services', 'consulting', 'financial services', 
  'healthcare', 'legal services', 'technology services'
];
const industryScore = highValueIndustries.some(industry => 
  companyData.industry?.toLowerCase().includes(industry.toLowerCase())
) ? 20 : 15;
leadScore += industryScore;

// Automation opportunities scoring (0-35 points)
if (opportunities.length > 0) {
  const avgPriorityScore = opportunities.reduce((sum, opp) => sum + (opp.priority_score || 0), 0) / opportunities.length;
  leadScore += Math.round(avgPriorityScore * 0.35);

  // Calculate total ROI potential
  totalROI = opportunities.reduce((sum, opp) => sum + (opp.annual_savings || 0), 0);

  // Determine recommended package based on ROI and complexity
  const highROIOpportunities = opportunities.filter(opp => opp.annual_savings > 15000).length;
  const complexOpportunities = opportunities.filter(opp => opp.automation_complexity === 'high').length;

  if (totalROI > 50000 || highROIOpportunities >= 3 || complexOpportunities >= 2) {
    recommendedPackage = 'enterprise_15k';
    priorityLevel = 'high';
  } else if (totalROI > 25000 || highROIOpportunities >= 1 || opportunities.length >= 3) {
    recommendedPackage = 'professional_7_5k';
    priorityLevel = 'medium';
  }
}

// Website analysis quality boost (0-10 points)
if (companyData.status === 'analyzed') {
  leadScore += 10;
}

// Revenue-based scoring (0-10 points)
if (companyData.annual_revenue_min) {
  if (companyData.annual_revenue_min > 10000000) leadScore += 10;
  else if (companyData.annual_revenue_min > 5000000) leadScore += 8;
  else if (companyData.annual_revenue_min > 1000000) leadScore += 6;
  else leadScore += 4;
}

// Cap lead score at 100
leadScore = Math.min(leadScore, 100);

// Determine priority level based on lead score
if (leadScore >= 80) priorityLevel = 'high';
else if (leadScore >= 60) priorityLevel = 'medium';
else priorityLevel = 'low';

// Generate business email
const domain = companyData.domain || companyData.website?.replace(/https?:\/\/(www\.)?/, '').split('/')[0];
const businessName = companyData.name?.replace(/[^a-zA-Z0-9\s]/g, '')?.split(' ')[0]?.toLowerCase() || 'business';
const emailOptions = [
  `info@${domain}`,
  `contact@${domain}`,
  `hello@${domain}`,
  `${businessName}@${domain}`,
  `admin@${domain}`
];
const prospectEmail = emailOptions[0]; // Use primary email

// Prepare comprehensive tags for segmentation
const tags = [
  `Lead-Score-${Math.floor(leadScore/10)*10}`, // e.g., "Lead-Score-80"
  `Package-${recommendedPackage.replace('_', '-')}`,
  `Priority-${priorityLevel.charAt(0).toUpperCase() + priorityLevel.slice(1)}`,
  `Industry-${companyData.industry?.replace(/[^a-zA-Z0-9]/g, '-') || 'Unknown'}`,
  `Size-${companyData.company_size_category || 'Unknown'}`,
  `ROI-${totalROI > 50000 ? 'High' : totalROI > 25000 ? 'Medium' : 'Low'}`,
  `Opportunities-${opportunities.length}`,
  `Source-Prospect-Intelligence`
].filter(Boolean);

// Determine lead temperature for Instantly
let leadTemperature = 'cold';
if (leadScore >= 85) leadTemperature = 'hot';
else if (leadScore >= 70) leadTemperature = 'warm';
else if (leadScore >= 50) leadTemperature = 'interested';

// Create custom fields for Instantly
const customFields = {
  company_name: companyData.name,
  website: companyData.website,
  industry: companyData.industry,
  company_size: companyData.company_size_category,
  employee_count: `${companyData.employee_count_min || 'Unknown'}-${companyData.employee_count_max || 'Unknown'}`,
  annual_revenue: companyData.annual_revenue_min ? `$${(companyData.annual_revenue_min / 1000000).toFixed(1)}M+` : 'Unknown',
  lead_score: leadScore,
  recommended_package: recommendedPackage,
  total_roi_potential: totalROI,
  automation_opportunities_count: opportunities.length,
  priority_level: priorityLevel,
  lead_temperature: leadTemperature,
  analysis_date: new Date().toISOString().split('T')[0],

  // Top 3 automation opportunities
  top_opportunity_1: opportunities[0]?.process_name || 'N/A',
  top_opportunity_1_savings: opportunities[0]?.annual_savings || 0,
  top_opportunity_2: opportunities[1]?.process_name || 'N/A', 
  top_opportunity_2_savings: opportunities[1]?.annual_savings || 0,
  top_opportunity_3: opportunities[2]?.process_name || 'N/A',
  top_opportunity_3_savings: opportunities[2]?.annual_savings || 0
};

// Prepare final CRM data for Instantly API
const instantlyData = {
  // Lead information
  email: prospectEmail,
  firstName: 'Business',
  lastName: 'Owner',
  companyName: companyData.name,

  // Instantly-specific fields
  leadScore: leadScore,
  leadTemperature: leadTemperature,
  priorityLevel: priorityLevel,

  // Package and ROI data
  recommendedPackage: recommendedPackage,
  totalROI: totalROI,
  opportunitiesCount: opportunities.length,

  // Tagging and segmentation
  tags: tags,

  // Custom fields for personalization
  customFields: customFields,

  // Campaign assignment data
  targetCampaign: null, // Will be determined in next step
  targetSequence: null, // Will be determined in next step

  // Metadata
  companyId: companyData.id,
  actionType: webhookData.actionType || 'analysis-completed',
  analysisTimestamp: new Date().toISOString()
};

return [{ json: instantlyData }];

// Intelligent campaign and sequence selection based on lead characteristics
const leadData = $input.first().json;
const leadScore = leadData.leadScore;
const recommendedPackage = leadData.recommendedPackage;
const totalROI = leadData.totalROI;
const industry = leadData.customFields.industry;
const companySize = leadData.customFields.company_size;
const opportunitiesCount = leadData.opportunitiesCount;

// Define campaign mapping based on lead profile
let campaignId = '';
let sequenceId = '';
let campaignName = '';
let sequenceName = '';
let delayHours = 2; // Default delay
let maxFollowUps = 5;

// High-value enterprise leads (score 80+, enterprise package, or high ROI)
if (leadScore >= 80 || recommendedPackage === 'enterprise_15k' || totalROI > 50000) {
  campaignId = 'entelech_enterprise_vip';
  sequenceId = 'seq_enterprise_executive';
  campaignName = 'Enterprise VIP - Executive Outreach';
  sequenceName = 'Executive Decision Maker Sequence';
  delayHours = 0.5; // 30 minute delay for immediate attention
  maxFollowUps = 7;
}
// High-potential professional leads (score 60-79, professional package)
else if (leadScore >= 60 || recommendedPackage === 'professional_7_5k' || (totalROI > 25000 && opportunitiesCount >= 2)) {
  campaignId = 'entelech_professional_priority';
  sequenceId = 'seq_professional_priority';
  campaignName = 'Professional Priority - High Value Prospects';
  sequenceName = 'Professional Service Automation Sequence';
  delayHours = 1; // 1 hour delay
  maxFollowUps = 6;
}
// Warm prospects with specific automation needs (score 40-59)
else if (leadScore >= 40 || opportunitiesCount >= 1) {
  campaignId = 'entelech_warm_prospects';
  sequenceId = 'seq_warm_nurture';
  campaignName = 'Warm Prospects - Automation Focused';
  sequenceName = 'Automation Opportunity Nurture';
  delayHours = 4; // 4 hour delay
  maxFollowUps = 5;
}
// Cold prospects or low-scoring leads
else {
  campaignId = 'entelech_cold_outreach';
  sequenceId = 'seq_cold_education';
  campaignName = 'Cold Outreach - Educational';
  sequenceName = 'Educational Automation Series';
  delayHours = 24; // 24 hour delay
  maxFollowUps = 4;
}

// Industry-specific adjustments
const highTouchIndustries = ['legal services', 'healthcare', 'financial services'];
if (highTouchIndustries.some(ind => industry?.toLowerCase().includes(ind.toLowerCase()))) {
  // Add industry-specific sequence variant
  sequenceId += '_compliance';
  sequenceName += ' (Compliance Focused)';
  delayHours = Math.max(delayHours, 2); // Minimum 2 hour delay for high-touch industries
}

// Size-based adjustments
if (companySize === 'enterprise' || companySize === 'large') {
  // Enterprise gets longer consideration time
  delayHours = Math.max(delayHours, 4);
}

// Special handling for different action types
if (leadData.actionType === 'opportunity-calculated') {
  campaignId += '_roi_report';
  campaignName += ' (ROI Report Available)';
  delayHours = Math.max(delayHours / 2, 0.25); // Reduce delay for ROI reports
}

// Personalization variables for email templates
const personalizationVars = {
  first_name: leadData.firstName,
  last_name: leadData.lastName,
  company_name: leadData.companyName,
  website: leadData.customFields.website,
  industry: industry,
  company_size: companySize,
  lead_score: leadScore,
  recommended_package: recommendedPackage.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase()),
  total_roi: totalROI > 0 ? `$${(totalROI/1000).toFixed(0)}K` : 'Significant',
  opportunities_count: opportunitiesCount,
  top_opportunity: leadData.customFields.top_opportunity_1,
  top_opportunity_savings: leadData.customFields.top_opportunity_1_savings > 0 ? 
    `$${(leadData.customFields.top_opportunity_1_savings/1000).toFixed(0)}K` : 'substantial',
  package_price: recommendedPackage === 'enterprise_15k' ? '$15K' : 
                 recommendedPackage === 'professional_7_5k' ? '$7.5K' : '$2.5K',
  urgency_level: leadScore >= 80 ? 'High' : leadScore >= 60 ? 'Medium' : 'Low'
};

const campaignAssignment = {
  // Campaign details
  campaignId: campaignId,
  sequenceId: sequenceId, 
  campaignName: campaignName,
  sequenceName: sequenceName,

  // Timing configuration
  delayHours: delayHours,
  maxFollowUps: maxFollowUps,

  // Personalization
  personalizationVars: personalizationVars,

  // Lead data to pass through
  leadData: leadData,

  // Assignment reasoning (for logging)
  assignmentReason: `Lead Score: ${leadScore}, Package: ${recommendedPackage}, ROI: $${(totalROI/1000).toFixed(0)}K, Industry: ${industry}`
};

return [{ json: campaignAssignment }];

